onClick:
{{ 
  UpdateStatus.run(
    () => { 
      showAlert('ปฏิเสธคำขอเรียบร้อย', 'success'); 
      closeModal('Modal_Reject');  // ปิด Modal

      // สั่งรีเฟรชตาราง (ใส่ชื่อ Query ดึงข้อมูลของคุณตรงนี้)
      Get_All_Requests.run(); 
    }, 
    () => showAlert('เกิดข้อผิดพลาด', 'error'), 
    { 
      // ส่งค่าไปให้ API
      id: appsmith.store.selected_request_id,  // ดึง ID ที่แปะไว้ตะกี้
      status: 'Rejected',
      reason: inp_reject_reason.text           // ดึงเหตุผลจากช่องกรอก
    } 
  ) 
}}
