.kanban-board {
  display: flex;
  gap: 24px;
  padding: 16px 8px;
  background: #f7fafc;
  border-radius: var(--appsmith-theme-borderRadius);
  min-height: 90vh;
  overflow-x: auto;
}

.kanban-column {
  background: #fff;
  min-width: 280px;
  max-width: 320px;
  border-radius: 12px;
  box-shadow: 0 0 0 1px #f0f0f0, 0 8px 32px rgba(0, 0, 0, 0.04);
  display: flex;
  flex-direction: column;
  padding: 14px;
}

.kanban-column h3 {
  font-size: 18px;
  margin: 0 0 10px 0;
  font-weight: 600;
  color: #132043;
  letter-spacing: 0.2px;
}

.kanban-cards {
  flex: 1;
  min-height: 40px;
}

.kanban-card {
  background: #f0f3fa;
  margin-bottom: 12px;
  border-radius: 6px;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.03);
  padding: 12px 14px;
  font-size: 15px;
  transition: box-shadow 0.2s;
  cursor: pointer;
  user-select: none;
  border-left: 4px solid var(--appsmith-theme-primaryColor, #5539FF);
}

.kanban-card.dragging {
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.16);
  background: #e9f2fa;
}

.kanban-add-card {
  background: #edf0f5;
  border: 1px dashed #c1c7d0;
  color: #555;
  border-radius: 6px;
  padding: 8px;
  margin-top: 8px;
  cursor: pointer;
  text-align: center;
  transition: background 0.2s;
}

.kanban-add-card:hover {
  background: #e2e6ee;
}

.kanban-input {
  width: 100%;
  border-radius: 5px;
  border: 1px solid #dde0ea;
  padding: 9px 8px;
  font-size: 15px;
  margin-top: 5px;
  margin-bottom: 5px;
}

.kanban-action-btn {
  background: var(--appsmith-theme-primaryColor, #5539FF);
  color: #fff;
  border: none;
  padding: 7px 14px;
  margin-right: 8px;
  border-radius: 4px;
  cursor: pointer;
  transition: background 0.2s;
  font-size: 15px;
}

.kanban-action-btn.cancel {
  background: #e0e4ee;
  color: #132043;
}

.kanban-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.kanban-title {
  font-size: 22px;
  font-weight: 700;
  color: var(--appsmith-theme-primaryColor, #5539FF);
}

