import { DragDropContext, Droppable, Draggable } from 'https://cdn.jsdelivr.net/npm/react-beautiful-dnd@13.1.1/+esm';

/*
 This Kanban Board widget uses a React Kanban pattern with drag-and-drop powered by react-beautiful-dnd.
 Columns and cards are stored in appsmith.model. Column order and card movement are fully functional.
*/

const fallbackModel = {
  columns: {
    todo: {
      id: 'todo',
      title: 'To do',
      cardIds: ['c1', 'c2'],
    },
    doing: {
      id: 'doing',
      title: 'In Progress',
      cardIds: ['c3'],
    },
    done: {
      id: 'done',
      title: 'Done',
      cardIds: ['c4'],
    },
  },
  cards: {
    c1: { id: 'c1', content: 'Design login page' },
    c2: { id: 'c2', content: 'Create DB schema' },
    c3: { id: 'c3', content: 'Develop API endpoints' },
    c4: { id: 'c4', content: 'Write documentation' },
  },
  columnOrder: ['todo', 'doing', 'done'],
};

function KanbanBoard() {
  const [model, setModel] = React.useState(appsmith.model && appsmith.model.columns ? appsmith.model : fallbackModel);
  const [addCardColumn, setAddCardColumn] = React.useState(null);
  const [newCardContent, setNewCardContent] = React.useState('');

  // Listen for external changes
  React.useEffect(() => {
    appsmith.onModelChange((newModel, prev) => {
      if (JSON.stringify(newModel) !== JSON.stringify(prev)) {
        setModel(newModel?.columns ? newModel : fallbackModel);
      }
    });
  }, []);

  // Handle drag and drop
  const onDragEnd = result => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }
    const start = model.columns[source.droppableId];
    const finish = model.columns[destination.droppableId];
    if (start === finish) {
      // Moving within same column
      const newCardIds = Array.from(start.cardIds);
      newCardIds.splice(source.index, 1);
      newCardIds.splice(destination.index, 0, draggableId);
      const newColumn = {
        ...start,
        cardIds: newCardIds,
      };
      const newModel = {
        ...model,
        columns: { ...model.columns, [newColumn.id]: newColumn},
      };
      setModel(newModel);
      appsmith.updateModel(newModel);
      appsmith.triggerEvent('onCardMove', { cardId: draggableId, from: start.id, to: finish.id });
      return;
    }
    // Moving between columns
    const startCardIds = Array.from(start.cardIds);
    startCardIds.splice(source.index, 1);
    const finishCardIds = Array.from(finish.cardIds);
    finishCardIds.splice(destination.index, 0, draggableId);
    const newStart = {
      ...start,
      cardIds: startCardIds,
    };
    const newFinish = {
      ...finish,
      cardIds: finishCardIds,
    };
    const newModel = {
      ...model,
      columns: {
        ...model.columns,
        [newStart.id]: newStart,
        [newFinish.id]: newFinish,
      },
    };
    setModel(newModel);
    appsmith.updateModel(newModel);
    appsmith.triggerEvent('onCardMove', { cardId: draggableId, from: start.id, to: finish.id });
  };

  // Add new card to column
  const handleAddCard = colId => {
    if (!newCardContent.trim()) return;
    const cardId = 'c' + Date.now();
    const newCard = { id: cardId, content: newCardContent };
    const updatedCards = { ...model.cards, [cardId]: newCard };
    const updatedCol = {
      ...model.columns[colId],
      cardIds: [...model.columns[colId].cardIds, cardId],
    };
    const updatedModel = {
      ...model,
      cards: updatedCards,
      columns: { ...model.columns, [colId]: updatedCol },
    };
    setModel(updatedModel);
    setAddCardColumn(null);
    setNewCardContent('');
    appsmith.updateModel(updatedModel);
    appsmith.triggerEvent('onAddCard', { columnId: colId, cardId, content: newCardContent });
  };

  // Render UI
  return (
    <div>
      <div className="kanban-header">
        <span className="kanban-title">Kanban Board</span>
      </div>
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="kanban-board">
          {model.columnOrder.map(colId => {
            const col = model.columns[colId];
            return (
              <div key={col.id} className="kanban-column">
                <h3>{col.title}</h3>
                <Droppable droppableId={col.id}>
                  {(provided, snapshot) => (
                    <div
                      className="kanban-cards"
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      style={{ minHeight: 48 }}
                    >
                      {col.cardIds.map((cid, idx) => {
                        const card = model.cards[cid];
                        return (
                          <Draggable key={cid} draggableId={cid} index={idx}>
                            {(provided, dragSnapshot) => (
                              <div
                                className={
                                  dragSnapshot.isDragging ?
                                    'kanban-card dragging' :
                                    'kanban-card'
                                }
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                              >
                                {card.content}
                              </div>
                            )}
                          </Draggable>
                        );
                      })}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
                {addCardColumn === col.id ? (
                  <div>
                    <input
                      className="kanban-input"
                      value={newCardContent}
                      autoFocus
                      placeholder="Enter card content"
                      onChange={e => setNewCardContent(e.target.value)}
                      onKeyDown={e => {
                        if (e.key === 'Enter') handleAddCard(col.id);
                        if (e.key === 'Escape') setAddCardColumn(null);
                      }}
                    />
                    <button
                      className="kanban-action-btn"
                      onClick={() => handleAddCard(col.id)}
                      style={{ marginBottom: 6 }}
                    >
                      Add
                    </button>
                    <button
                      className="kanban-action-btn cancel"
                      onClick={() => setAddCardColumn(null)}
                    >
                      Cancel
                    </button>
                  </div>
                ) : (
                  <div
                    className="kanban-add-card"
                    onClick={() => {
                      setAddCardColumn(col.id);
                      setNewCardContent('');
                    }}
                    style={{ marginTop: 8 }}
                  >
                    + Add Card
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </DragDropContext>
    </div>
  );
}

appsmith.onReady(() => {
  ReactDOM.render(<KanbanBoard />, document.getElementById('root'));
});
